import React, { useState, useEffect } from 'react';
import { Button, Card, CardGroup } from 'react-bootstrap';

const GetAppointments = () => {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch appointments from the backend API
  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/getallappointments'); // Replace with your actual backend URL
        const data = await response.json();
        
        if (response.ok) {
          setAppointments(data.appointments);
        } else {
          setError(data.message || 'Something went wrong');
        }
      } catch (err) {
        setError('Error fetching appointments');
      } finally {
        setLoading(false);
      }
    };

    fetchAppointments();
  }, []);

  if (loading) {
    return <div>Loading appointments...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Appointments</h1>
      <CardGroup className="Card-group">
        {appointments.map((appointment, index) => (
          <Card key={index}>
            <Card.Body>
              <Card.Title>{appointment.name}</Card.Title>
              <Card.Subtitle className="mb-2 text-muted">{appointment.email}</Card.Subtitle>
              <Card.Text>
                <strong>Appointment Date:</strong> {appointment.appointmentDate} <br />
                <strong>Appointment Time:</strong> {appointment.appointmentTime} <br />
                <strong>Comments:</strong> {appointment.comments}
              </Card.Text>
              <Button variant="primary" href={`#appointment-${index}`}>View Appointment Details</Button>
            </Card.Body>
          </Card>
        ))}
      </CardGroup>
    </div>
  );
};

export default GetAppointments;
